import './App.css';
import { useState, useEffect } from 'react';
import  Axios  from 'axios';

function App() {
  const [movieList, setMovieList] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:8000/api/getMovies").then((response) => {
      setMovieList(response.data);
    });
  }, []);

  return (
    <>
    <div className="App">
    <Nav />
      <Routes>
          <Route exact path="/" element={<Home movies={movieList} setMovies={setMovieList} />} />
          <Route exact path="/leavereview" element={<LeaveReview movies={listOfMovies} setMovies={setListOfMovies}/>}/>
          <Route path="*" element={<NotFoundPage/>}/>
      </Routes>
    </div>
    <Footer />
  </>
  );
}

export default App;
