import React from "react";
import './App.css';
import {Home, LeaveReview, NotFoundPage, Nav} from './pages';
import {Routes, Route } from "react-router-dom";
import { useState, useEffect } from 'react';



function App(){
  const [JSONmovies, setJSONMovies] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect( () => {
    setLoading(true);
    fetch("/movies.json") 
    .then( response => response.json() ) 
    .then( setJSONMovies )
    .then(() => setLoading(false))
    .then( console.log(JSONmovies))
    .catch( e => console.log(e.message) );

  },[]);

  const [movies, setMovies] = useState([]);
  const movieList = JSONmovies.map((movie, i) => ({id:i}));
  setMovies( movieList)

  if (loading) return <h1>Loading...</h1>;

  if (movies){
    return (
      <div className="App">
      <Nav />
      <Routes>
          <Route exact path="/" element={<Home movies={movies} setMovies={setMovies} />} />
          <Route exact path="/leavereview" element={<LeaveReview movies={movies} setMovies={setMovies}/>}/>
          <Route path="*" element={NotFoundPage}/>
      </Routes>
      </div>
    );
  }
  return <div>No Data Available</div>
}

export default App;
