import React, { useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Axios from "axios";
import { useForm } from "react-hook-form";

export function LeaveReview(props){
    const { register, handleSubmit, reset, formState: { errors } } = useForm();
    const [success, setSuccess] = useState(false);
    useEffect(() => {
            reset({title: "", release: "", actors: "", rating: ""})
        }, [success]);

    const movies = props.movies;
    const setMovies = props.setMovies;
    // const [title, setTitle] = useState("");
    // const [release, setRelease] = useState("");
    // const [actors, setActors] = useState([]);
    // const [rating, setRating] = useState(0);
    // const [image, setImage] = useState("");

    const createMovie = (data) => {
        Axios.post("http://localhost:8000/api/createMovie", {title: title, release: release, actors: actors, rating: rating, image: image}).then((response) => {
          setMovies([...movies, {title: title, release: release, actors: actors, rating: rating, image: image}])
        });
        setSuccess(true);
        alert("Movie has been added successfully!");
      };
      return (
        <div className="container">
            <h1><strong>LEAVE A REVIEW</strong></h1>
            <div className="formContainer">
                    <div class="form-group">
                        <label>TITLE:</label><input className="input-group"{...register("title", {required: true, minLength: 4})} />
                         <span className="input-group-text">Please enter atleast 4 characters</span>
                    </div>
                    <div class="form-group">
                        <label>RELEASE:</label><input className="input-group" onChange={(event) => {setRelease(event.target.value)}} />
                        <span className="input-group-text">Please enter atleast 4 characters</span>
                    </div>
                    <div class="form-group">
                        <label>ACTORS:</label><input className="input-group" onChange={(event) => {setActors(event.target.value)}} />
                        <span className="input-group-text">Please enter atleast 10 characters</span>
                    </div>
                    <div class="form-group">
                        <label>RATING:</label><br/><input style={{maxWidth:"100px" ,textAlign: "center"}}type="number" onChange={(event) => {setRating(event.target.value)}}/>
                        <span className="input-group-text">Rate from 0-5</span>
                    </div>
                    <br></br>
                    <label>SELECT IMAGE:</label><br/>
                    <select className="drop-down"  onChange={(event) => {setImage(event.target.value)}}>
                        <option className="selection" defaultValue="" disabled selected></option>
                        <option value='/movieImages/generic.jpeg'>Other</option>
                        <option value='/movieImages/forrestgump.jpeg'>Forrest Gump</option>
                        <option value='/movieImages/inception.jpeg'>Inception</option>
                        <option value='/movieImages/lambs.jpeg'>Silence of the Lambs</option>
                        <option value='/movieImages/shawshank.jpg'>The Shawshank Redemption</option>
                        <option value='/movieImages/darkknight.jpg'>The Dark Knight</option>
                        <option value='/movieImages/godfather.jpg'>The Godfather</option>
                        <option value='/movieImages/fightclub.jpg'>Fight Club</option>   
                    </select><br/><br/>
                    <input className="btn-primary" onClick = {createMovie}type="submit" />
            </div>
        </div>
        )};
