import React, { useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Axios from "axios";


export function Home(){

    const [movieList, setMovieList] = useState([]);
    useEffect(() => {
        reset({title: "", release: "", actors: "", rating: ""})
    }, [success])
    useEffect(() => {
      Axios.get("http://localhost:8000/api/getMovies").then((response) => {
        setMovieList(response.data);
      });
    }, []);

    const removeMovie = (event) => {
       const movieID = event.target.title;
       setMovieList(movieList.filter(movie => movie.title !== movieID));
       };

    if(movieList.length > 0){
        return (
            <div className="container">
            <div style={{paddingBottom: "100px"}} id="align-content-*-center">
                <h1><strong>MOVIE REVIEWS</strong></h1>
                <hr></hr>
                <ul style={{listStyleType: "none", textAlign: "center"  }}>
                {movieList.map(movie=> ( 
                    <>
                    <li key={movie.__id}><h4><strong>{movie.title}</strong></h4></li>
                    <li key={movie.__id}>Release: {movie.release}</li>
                    <li key={movie.__id}>Actors: {movie.actors}</li>
                    <li key={movie.__id}>Rating: {movie.rating}/5</li>
                    <img src={movie.image} alt="movie poster"></img><br /><br />
                    <button className= "btn-primary" onClick = {removeMovie}>Remove Movie</button>               
                    <hr></hr>
                    </>
                ))}
                </ul>
            </div>
        </div>
    )};

    return (
        <p style={{paddingBottom: "500px"}}><h1>NO AVAILABLE MOVIE REVIEWS</h1></p>
    )};
